#!/usr/bin/env python3

from os.path import expanduser, join
from subprocess import run
import pathlib

installdir = join(expanduser("~"), pathlib.Path(__file__).parent.absolute())

scripts = [
"https://raw.githubusercontent.com/Irrational-Encoding-Wizardry/fvsfunc/master/fvsfunc.py",
"https://raw.githubusercontent.com/HomeOfVapourSynthEvolution/havsfunc/master/havsfunc.py",
"https://raw.githubusercontent.com/HomeOfVapourSynthEvolution/mvsfunc/master/mvsfunc.py",
"https://raw.githubusercontent.com/WolframRhodium/muvsfunc/master/muvsfunc.py",
"https://raw.githubusercontent.com/Irrational-Encoding-Wizardry/kagefunc/master/kagefunc.py",
"https://gist.githubusercontent.com/4re/342624c9e1a144a696c6/raw/08df48752c70c66df4127f2791bbedbc1958da77/nnedi3_rpow2.py",
"https://gist.githubusercontent.com/YamashitaRen/020c497524e794779d9c/raw/2a20385e50804f8b24f2a2479e2c0f3c335d4853/edi_rpow2.py",
"https://raw.githubusercontent.com/dubhater/vapoursynth-adjust/master/adjust.py",
"https://raw.githubusercontent.com/HomeOfVapourSynthEvolution/vsTAAmbk/master/vsTAAmbk.py",
"https://raw.githubusercontent.com/dubhater/vapoursynth-xaa/master/xaa.py",
"https://raw.githubusercontent.com/Irrational-Encoding-Wizardry/vapoursynth-descale/master/descale.py",
"https://gist.githubusercontent.com/cN3rd/51077b6abf45b684bf9a3c657d859b43/raw/bbd7d639e72e8ebf9a6b10e723b762f53055a563/getscaler.py"
]

for script in scripts:
    filename = script.split("/")[-1]
    run(["curl", script, "--output", join(installdir, filename)])
